import base64
import weakref
from io import BytesIO

import requests
from PIL import JpegImagePlugin


class BrowserImage(object):
    _resources = weakref.WeakValueDictionary()

    def __new__(cls, img=None, img_url=None, *args, **kwargs):
        if img_url:
            # loading img from http
            resp = requests.get(img_url)
            img_data = resp.content
        else:
            ind = 'base64,'
            if img.find(ind) == -1:
                # loading img from bytes
                img_data = img
            else:
                # loading img from b64encoded string
                img_data = base64.b64decode(img[img.index(ind) + len(ind):])
        key = hash(img_data)
        if key in BrowserImage._resources:
            return BrowserImage._resources[key]
        loaded_img = JpegImagePlugin.Image.open(BytesIO(img_data))
        obj = object.__new__(cls)
        obj.set_image(loaded_img)
        BrowserImage._resources[key] = obj
        return obj

    def set_image(self, loded_image):
        self._loaded_image = loded_image

    def get_image(self):
        return self._loaded_image


class HttpLink(object):
    def __init__(self, url, label='', img=None, img_url=None):
        self.image = BrowserImage(img, img_url)
        self.label = ''

    def show_image(self):
        self.image.get_image().show()


